<script type='text/javascript'>
// Liquid Full Screen Menu scripts: 
(function() {
  //Hamburger menu wrapper
  const elmHamburger = document.querySelector('#uc_uc_liquid_full_screen_menu_elementor16268 .uc_liquid_hamburger');
  // Menu items
  const gNavItems = document.querySelectorAll('#uc_uc_liquid_full_screen_menu_elementor16268 .menu-item');
  //Overlay Elements
  const elmOverlay = document.querySelector('#uc_uc_liquid_full_screen_menu_elementor16268 .shape-overlays');
  // Shape overlay class
  const overlay = new LiquidFullScreenMenuStyle1(elmOverlay);;
  
  var objNavItems = jQuery(gNavItems);
  var objLinks = objNavItems.find("a");
  
  objLinks.click(function(){

    var objLink = jQuery(this);
    var url = objLink.attr("href");

    url = jQuery.trim(url);
	
    if(!url)
      return(true);

    if(url[0] == "#")
      jQuery(elmHamburger).trigger("click");
	
    return(true);
  });
  
    
  elmHamburger.addEventListener('click', () => {
    if (overlay.isAnimating) {
      return false;
    }
    overlay.toggle();
    if (overlay.isOpened === true) {      
      elmHamburger.classList.add('is-opened-navi');
      for (var i = 0; i < gNavItems.length; i++) {
        gNavItems[i].classList.add('is-opened');
      }
      // Prevent body scrolling while overlay is open
      	document.body.style.overflow = 'hidden'; 
    } else {
      // Re-enable body scrolling when overlay is closed
      document.body.style.overflow = 'auto'; 
      
      elmHamburger.classList.remove('is-opened-navi');
      for (var i = 0; i < gNavItems.length; i++) {
        gNavItems[i].classList.remove('is-opened');
      }
    }
  });
}());
